import { useState } from "react"

const useToast = () => {
  const [toast, setToast] = useState({
    message: "",
    type: "",
  })

  const showToast = (message, type) => {
    setToast({ message, type })

    setTimeout(() => {
      clearToast
    }, 3000)
  }

  const clearToast = () => {
    setToast({ message: "", type: "" })
  }

  return { showToast, toast, clearToast }
}

export default useToast
