import { useState } from "react"

const useToast = () => {
  const [toast, setToast] = useState({
    message: "",
    type: "",
  })

  const showToast = (message, type) => {
    setToastMessage({ message, type })

    setTimeout(() => {
      setToastMessage({
        message: "",
        type: "",
      })
    }, 3000)
  }
  return { showToast, toast }
}

export default useToast
